import React, { useState, useEffect } from "react";
import axios from "axios";

const UDashboard = () => {
  const [user, setUser] = useState(null); // User details
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    email: "",
    address: "",
    gender: "",
    dob: "",
  }); // For form inputs
  const [isEditing, setIsEditing] = useState(false); // Toggle editing state
  const [error, setError] = useState(null); // Error messages
  const [loading, setLoading] = useState(true); // Loading state

  const userId = "CURRENT_USER_ID"; // Replace with the logged-in user's ID, fetched from authentication/session

  // Fetch the logged-in user's profile
  const fetchUserProfile = () => {
    setLoading(true);
    axios
      .get(`http://localhost:2323/users/${userId}`)
      .then((res) => {
        setUser(res.data);
        setFormData({
          name: res.data.name,
          mobile: res.data.mobile,
          email: res.data.email,
          address: res.data.address || "",
          gender: res.data.gender,
          dob: res.data.dob.split("T")[0], // Format date
        });
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchUserProfile();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Submit updated profile
  const handleUpdate = (e) => {
    e.preventDefault();

    // Validation
    if (
      !formData.name ||
      !formData.mobile ||
      !formData.email ||
      !formData.gender ||
      !formData.dob
    ) {
      setError("Please fill all required fields");
      return;
    }

    axios
      .put(`http://localhost:2323/users/${userId}`, formData)
      .then(() => {
        setIsEditing(false);
        fetchUserProfile(); // Refresh data
        setError(null);
      })
      .catch((err) => setError(err.message));
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container">
      <h1>User Dashboard</h1>

      {isEditing ? (
        // Edit Form
        <form onSubmit={handleUpdate}>
          <div className="mb-3">
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <div className="mb-3">
            <label>Mobile</label>
            <input
              type="tel"
              name="mobile"
              value={formData.mobile}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <div className="mb-3">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <div className="mb-3">
            <label>Address</label>
            <textarea
              name="address"
              value={formData.address}
              onChange={handleChange}
              className="form-control"
            ></textarea>
          </div>
          <div className="mb-3">
            <label>Gender</label>
            <select
              name="gender"
              value={formData.gender}
              onChange={handleChange}
              className="form-control"
              required
            >
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <div className="mb-3">
            <label>Date of Birth</label>
            <input
              type="date"
              name="dob"
              value={formData.dob}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary me-2">
            Save
          </button>
          <button
            type="button"
            onClick={() => setIsEditing(false)}
            className="btn btn-secondary"
          >
            Cancel
          </button>
        </form>
      ) : (
        // Profile Display
        <div>
          <h2>Profile</h2>
          <p>
            <strong>Name:</strong> {user.name}
          </p>
          <p>
            <strong>Mobile:</strong> {user.mobile}
          </p>
          <p>
            <strong>Email:</strong> {user.email}
          </p>
          <p>
            <strong>Address:</strong> {user.address || "N/A"}
          </p>
          <p>
            <strong>Gender:</strong> {user.gender}
          </p>
          <p>
            <strong>Date of Birth:</strong>{" "}
            {new Date(user.dob).toLocaleDateString()}
          </p>
          <button
            onClick={() => setIsEditing(true)}
            className="btn btn-primary"
          >
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );
};

export default UDashboard;
