import React, { useState, useEffect } from "react";
import axios from "axios";

const SAD = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    role: "",
    mobile: "",
    email: "",
    address: "",
    gender: "",
    dob: "",
    profilePicture: "",
  });
  const [editing, setEditing] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [file, setFile] = useState(null); // State for file upload

  const fetchUsers = () => {
    setLoading(true);
    axios
      .get("http://localhost:2323/users")
      .then((res) => {
        const userData = Array.isArray(res.data) ? res.data : [];
        setUsers(userData);
        setFilteredUsers(userData);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    if (query.trim() === "") {
      setFilteredUsers(users);
    } else {
      const filtered = users.filter((user) =>
        user.name.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredUsers(filtered);
    }
  };

  // Handle file upload
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("profilePicture", file);

    try {
      const res = await axios.post("http://localhost:2323/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setFormData((prevData) => ({
        ...prevData,
        profilePicture: res.data.filePath,
      }));
      setError(null);
    } catch (err) {
      setError("File upload failed. Please try again.");
    }
  };

  if (loading) return <div className="text-center my-5">Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Super Admin Dashboard</h1>

      <div className="mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Search by Name"
          value={searchQuery}
          onChange={handleSearchChange}
        />
      </div>

      {/* File Upload */}
      <div className="mb-4">
        <label htmlFor="fileUpload" className="form-label">
          Upload Profile Picture
        </label>
        <input
          type="file"
          id="fileUpload"
          className="form-control"
          onChange={handleFileChange}
        />
        <button className="btn btn-primary mt-2" onClick={handleFileUpload}>
          Upload
        </button>
      </div>

      <h2 className="text-center mb-4">Users</h2>
      <div className="row">
        {filteredUsers.map((user) => (
          <div key={user.id} className="col-md-6 col-lg-4 mb-4">
            <div className="card h-100 shadow">
              <div className="card-body">
                <h5 className="card-title">{user.name}</h5>
                <p className="card-text">Role: {user.role}</p>
                <p className="card-text">Mobile: {user.mobile}</p>
                <p className="card-text">Email: {user.email}</p>
                <p className="card-text">Gender: {user.gender}</p>
                <p className="card-text">
                  DOB: {new Date(user.dob).toLocaleDateString()}
                </p>
                {user.address && (
                  <p className="card-text">Address: {user.address}</p>
                )}
                {user.profilePicture && (
                  <img
                    src={`http://localhost:2323${user.profilePicture}`}
                    alt="Profile"
                    className="img-fluid rounded-circle"
                  />
                )}
                <div className="d-flex justify-content-between mt-3">
                  <button
                    className="btn btn-primary"
                    onClick={() => {
                      setEditing(user.id);
                      setFormData(user);
                    }}
                  >
                    Edit
                  </button>
                  <button className="btn btn-danger">Delete</button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SAD;

