import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // For navigation after login

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:2323/user", {
        email,
        password,
      });
      const { name, role } = res.data; // Assuming backend returns a token and role
      localStorage.setItem("name", token); // Save token in local storage for auth
      localStorage.setItem("role", role); // Save role for access control

      // Navigate based on role
      if (role === "Super Admin") {
        navigate("/dashboard/superadmin");
      } else if (role === "Admin") {
        navigate("/dashboard/admin");
      } else if (role === "User") {
        navigate("/profile");
      }
    } catch (err) {
      console.log(err);
      setError("Invalid email or password!");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px" }}>
      <h2>Login</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
