import React, { useState, useEffect } from "react";
import axios from "axios";

const UDashboard = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    email: "",
    address: "",
    gender: "",
    dob: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  // Retrieve user ID from sessionStorage
  const userId = sessionStorage.getItem("currentUserId");

  // Redirect if no user ID is found in sessionStorage
  useEffect(() => {
    if (!userId) {
      setError("User not logged in. Redirecting...");
      setTimeout(() => (window.location.href = "/login"), 2000);
    }
  }, [userId]);

  // Fetch the logged-in user's profile
  const fetchUserProfile = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:2323/users/${userId}`);
      setUser(response.data);
      setFormData({
        name: response.data.name,
        mobile: response.data.mobile,
        email: response.data.email,
        address: response.data.address || "",
        gender: response.data.gender,
        dob: response.data.dob.split("T")[0],
      });
      setError(null);
    } catch (err) {
      setError("Failed to fetch user profile. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (userId) fetchUserProfile();
  }, [userId]);

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Submit updated profile
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (
      !formData.name ||
      !formData.mobile ||
      !formData.email ||
      !formData.gender ||
      !formData.dob
    ) {
      setError("Please fill all required fields");
      return;
    }

    try {
      await axios.put(`http://localhost:2323/users/${userId}`, formData);
      setIsEditing(false);
      fetchUserProfile(); // Refresh data
      setError(null);
    } catch (err) {
      setError("Failed to update profile. Please try again.");
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container">
      <h1>User Dashboard</h1>

      {isEditing ? (
        <form onSubmit={handleUpdate}>{/* Form fields */}</form>
      ) : (
        <div>
          <h2>Profile</h2>
          <p>
            <strong>Name:</strong> {user?.name || "N/A"}
          </p>
          <p>
            <strong>Mobile:</strong> {user?.mobile || "N/A"}
          </p>
          <p>
            <strong>Email:</strong> {user?.email || "N/A"}
          </p>
          <p>
            <strong>Address:</strong> {user?.address || "N/A"}
          </p>
          <p>
            <strong>Gender:</strong> {user?.gender || "N/A"}
          </p>
          <p>
            <strong>Date of Birth:</strong>{" "}
            {user?.dob ? new Date(user.dob).toLocaleDateString() : "N/A"}
          </p>
          <button
            onClick={() => setIsEditing(true)}
            className="btn btn-primary"
          >
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );
};

export default UDashboard;

