import React, { useState, useEffect } from "react";
import axios from "axios";

const UDashboard = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    email: "",
    address: "",
    gender: "",
    dob: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  // Get the logged-in user's ID from sessionStorage
  const userId = sessionStorage.getItem("currentUserId");

  // Fetch the logged-in user's profile
  const fetchUserProfile = () => {
    setLoading(true);
    axios
      .get(`http://localhost:2323/users/${userId}`)
      .then((res) => {
        setUser(res.data);
        setFormData({
          name: res.data.name,
          mobile: res.data.mobile,
          email: res.data.email,
          address: res.data.address || "",
          gender: res.data.gender,
          dob: res.data.dob.split("T")[0], // Format date
        });
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchUserProfile();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Submit updated profile
  const handleUpdate = (e) => {
    e.preventDefault();
    if (
      !formData.name ||
      !formData.mobile ||
      !formData.email ||
      !formData.gender ||
      !formData.dob
    ) {
      setError("Please fill all required fields");
      return;
    }

    axios
      .put(`http://localhost:2323/users/${userId}`, formData)
      .then(() => {
        setIsEditing(false);
        fetchUserProfile();
        setError(null);
      })
      .catch((err) => setError(err.message));
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container">
      <h1>User Dashboard</h1>

      {isEditing ? (
        <form onSubmit={handleUpdate}>{/* Form fields */}</form>
      ) : (
        <div>{/* Display Profile */}</div>
      )}
    </div>
  );
};

export default UDashboard;

