import React, { useState, useEffect } from "react";
import axios from "axios";

const EditUsers = () => {
  const [users, setUsers] = useState([]);
  const [editUserId, setEditUserId] = useState(null); // For tracking which user is being edited
  const [updatedName, setUpdatedName] = useState(""); // For the new name
  const [updatedRole, setUpdatedRole] = useState(""); // For the new role

  // Fetch users from backend
  useEffect(() => {
    axios
      .get("http://localhost:2323/users")
      .then((res) => setUsers(res.data))
      .catch((err) => console.log(err));
  }, []);

  // Function to start editing
  const startEdit = (user) => {
    setEditUserId(user.id);
    setUpdatedName(user.name);
    setUpdatedRole(user.role);
  };

  // Cancel edit mode
  const cancelEdit = () => {
    setEditUserId(null);
    setUpdatedName("");
    setUpdatedRole("");
  };

  // Update the user in the backend
  const saveUser = (id) => {
    axios
      .put(`http://localhost:2323/users/${id}`, {
        name: updatedName,
        role: updatedRole,
      })
      .then(() => {
        setUsers(
          users.map((user) =>
            user.id === id
              ? { ...user, name: updatedName, role: updatedRole }
              : user
          )
        );
        cancelEdit(); // Exit edit mode
      })
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <h1>User List</h1>
      {nusers ? (
        <div>
          {users.map((user) => (
            <div className="border-dark border m-1 container" key={user.id}>
              {editUserId === user.id ? (
                <>
                  <input
                    type="text"
                    value={updatedName}
                    onChange={(e) => setUpdatedName(e.target.value)}
                    placeholder="New Name"
                  />
                  <input
                    type="text"
                    value={updatedRole}
                    onChange={(e) => setUpdatedRole(e.target.value)}
                    placeholder="New Role"
                  />
                  <button onClick={() => saveUser(user.id)}>Save</button>
                  <button onClick={cancelEdit}>Cancel</button>
                </>
              ) : (
                <>
                  <p>
                    {user.name} - {user.role}
                  </p>
                  <button onClick={() => startEdit(user)}>Edit</button>
                </>
              )}
            </div>
          ))}
        </div>
      ) : (
        <></>
      )}
    </div>
  );
};

export default EditUsers;
