import React, { useState, useEffect } from "react";
import axios from "axios";

const UDashboard = ({ userId }) => {
  const [user, setUser] = useState(null); // To store user data
  const [formData, setFormData] = useState({
    name: "",
    role: "",
    mobile: "",
    email: "",
    address: "",
    gender: "",
    dob: "",
    profilePicture: "",
  }); // For profile editing
  const [editing, setEditing] = useState(false); // Toggle between view and edit mode
  const [error, setError] = useState(null); // Error messages
  const [loading, setLoading] = useState(true); // Loading state

  // Fetch user data by ID
  const fetchUser = () => {
    setLoading(true);
    axios
      .get(`http://localhost:2323/users/${userId}`)
      .then((res) => {
        setUser(res.data);
        setFormData(res.data); // Populate form with user data
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchUser();
  }, [userId]);

  // Handle form inputs
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Submit updated profile data
  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !formData.name ||
      !formData.mobile ||
      !formData.email ||
      !formData.gender ||
      !formData.dob
    ) {
      setError("Please fill all required fields.");
      return;
    }

    axios
      .put(`http://localhost:2323/users/${userId}`, formData)
      .then(() => {
        setEditing(false);
        fetchUser(); // Refresh user data
      })
      .catch((err) => setError(err.message));
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">User Dashboard</h1>

      {/* Profile View/Edit */}
      <div className="card shadow mb-5">
        <div className="card-body">
          <h2 className="card-title text-center">Your Profile</h2>
          {!editing ? (
            <div>
              {user.profilePicture && (
                <div className="text-center mb-4">
                  <img
                    src={user.profilePicture}
                    alt={`${user.name}'s profile`}
                    className="rounded-circle"
                    style={{ width: "150px", height: "150px" }}
                  />
                </div>
              )}
              <p>
                <strong>Name:</strong> {user.name}
              </p>
              <p>
                <strong>Role:</strong> {user.role}
              </p>
              <p>
                <strong>Mobile:</strong> {user.mobile}
              </p>
              <p>
                <strong>Email:</strong> {user.email}
              </p>
              <p>
                <strong>Gender:</strong> {user.gender}
              </p>
              <p>
                <strong>DOB:</strong> {new Date(user.dob).toLocaleDateString()}
              </p>
              {user.address && (
                <p>
                  <strong>Address:</strong> {user.address}
                </p>
              )}

              <button
                className="btn btn-primary mt-3"
                onClick={() => setEditing(true)}
              >
                Edit Profile
              </button>
            </div>
          ) : (
            <form onSubmit={handleSubmit}>
              <div className="row g-3">
                <div className="col-md-6">
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Name"
                    className="form-control"
                    required
                  />
                </div>
                <div className="col-md-6">
                  <input
                    type="tel"
                    name="mobile"
                    value={formData.mobile}
                    onChange={handleChange}
                    placeholder="Mobile"
                    className="form-control"
                    required
                  />
                </div>
                <div className="col-md-6">
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Email"
                    className="form-control"
                    required
                  />
                </div>
                <div className="col-md-6">
                  <select
                    name="gender"
                    value={formData.gender}
                    onChange={handleChange}
                    className="form-control"
                    required
                  >
                    <option value="">Select Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
                <div className="col-md-6">
                  <input
                    type="date"
                    name="dob"
                    value={formData.dob}
                    onChange={handleChange}
                    className="form-control"
                    required
                  />
                </div>
                <div className="col-md-6">
                  <input
                    type="text"
                    name="profilePicture"
                    value={formData.profilePicture}
                    onChange={handleChange}
                    placeholder="Profile Picture URL"
                    className="form-control"
                  />
                </div>
                <div className="col-12">
                  <textarea
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    placeholder="Address"
                    className="form-control"
                  ></textarea>
                </div>
                <div className="col-12 d-flex justify-content-end">
                  <button type="submit" className="btn btn-success me-2">
                    Save Changes
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setEditing(false)}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default UDashboard;

