import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import axios from "axios";

function LogPage() {
  const [mail, setMail] = useState("");
  const [pswd, setPswd] = useState("");
  const [data, setData] = useState([]);
  const [error, setError] = useState(""); // State to handle login errors

  const navigate = useNavigate();

  // Fetch data from the server
  useEffect(() => {
    axios
      .get("http://localhost:3232/users")
      .then(res => setData(res.data))
      .catch(err => console.log(err));
  }, []);

  // Function to handle login form submission
  const handleLogin = (e) => {
    e.preventDefault(); // Prevent page reload on form submit

    // Check if email and password match any user in the fetched data
    const user = data.find(
      (user) => user.name === mail && user.role === pswd
    );

    if (user) {
      // If user found, navigate to the next page (e.g., user dashboard)
      navigate("/dashboard", { state: { user } });
    } else {
      // If no match, show an error message
      setError("Invalid email or password");
    }
  };

  return (
    <div>
      <div className="border border-black col-lg-3 mx-auto my-auto p-3 mt-5">
        <form onSubmit={handleLogin}>
          <h4 className="text-start" style={{ fontFamily: "fantasy" }}>
            Login to DevAgentHub
          </h4>
          <br />
          <input
            onChange={(e) => setMail(e.target.value)}
            className="form-control mt-3"
            type=""
            required
            placeholder="Email"
          />
          <input
            onChange={(e) => setPswd(e.target.value)}
            className="form-control mt-3 mb-0"
            type="password"
            required
            placeholder="Password"
          />
          <p className="m-0">
            <a
              href="/"
              className="m-0"
              style={{ textDecoration: "none", fontSize: "smaller" }}
            >
              Forgot Password?
            </a>
          </p>
          <br />
          <button className="btn btn-primary form-control mb-3" type="submit">
            Login
          </button>

          {/* Show error if login fails */}
          {error && <p className="text-danger">{error}</p>}

          <p className="m-0" style={{ fontSize: "smaller" }}>
            Don't have an account? &nbsp;
            <a
              href="/signuppage"
              className="m-0"
              style={{ textDecoration: "none" }}
            >
              Signup
            </a>
          </p>
        </form>
      </div>
    </div>
  );
}

export default LogPage;
