import { useState } from "react";

const LogPage = () => {
  const [formData, setFormData] = useState({
    username: "",
    role: "",
  });
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");

    try {
      // Fetch user data to verify credentials
      const response = await fetch("http://localhost:2323/users");
      if (!response.ok) {
        throw new Error("Failed to fetch user data");
      }
      const users = await response.json();

      // Find user matching both username and role
      const user = users.find(
        (u) =>
          u.name.toLowerCase() === formData.username.toLowerCase() &&
          u.role === formData.role
      );

      if (user) {
        // Store user info in sessionStorage for persistence
        sessionStorage.setItem(
          "currentUser",
          JSON.stringify({
            id: user.id,
            name: user.name,
            role: user.role,
            email: user.email,
          })
        );

        // Navigate based on role
        switch (user.role) {
          case "Super Admin":
            window.location.href = "/sadashboard";
            break;
          case "Admin":
            window.location.href = "/adashboard";
            break;
          case "User":
            window.location.href = "/user/dashboard";
            break;
          default:
            window.location.href = "/dashboard";
        }
      } else {
        setError("Invalid username or role combination");
      }
    } catch (err) {
      console.error("Login error:", err);
      setError(err.message || "An error occurred during login");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center align-items-center min-vh-100">
        <div className="col-md-6 col-lg-4">
          <div className="card shadow">
            <div className="card-body p-4">
              <h2 className="text-center mb-4">Login to DevAgentHub</h2>

              <form onSubmit={handleLogin}>
                {error && (
                  <div className="alert alert-danger" role="alert">
                    {error}
                  </div>
                )}

                <div className="mb-3">
                  <label htmlFor="username" className="form-label">
                    Username
                  </label>
                  <input
                    id="username"
                    name="username"
                    type="text"
                    className="form-control"
                    placeholder="Enter your username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="role" className="form-label">
                    Role
                  </label>
                  <select
                    id="role"
                    name="role"
                    className="form-control"
                    value={formData.role}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Role</option>
                    <option value="Super Admin">Super Admin</option>
                    <option value="Admin">Admin</option>
                    <option value="User">User</option>
                  </select>
                </div>

                <button
                  type="submit"
                  className="btn btn-primary w-100 mb-3"
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Logging in...
                    </>
                  ) : (
                    "Sign in"
                  )}
                </button>

                <div className="text-center mb-3">
                  <a href="/forgot-password" className="text-decoration-none">
                    Forgot password?
                  </a>
                </div>

                <div className="text-center">
                  <span>Don't have an account? </span>
                  <a href="/signup" className="text-decoration-none">
                    Sign up
                  </a>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LogPage;
