import React, { useState, useEffect } from "react";
import axios from "axios";

const EditUsers = () => {
  const [users, setUsers] = useState([]);
  const [editing, setEditing] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    role: "",
    mobile: "",
    email: "",
    address: "",
    gender: "",
    dob: "",
  });

  useEffect(() => {
    axios
      .get("http://localhost:2323/users")
      .then((res) => setUsers(res.data))
      .catch((err) => console.log(err));
  }, []);

  const handleEdit = (user) => {
    setEditing(user.id);
    setFormData({
      name: user.name,
      role: user.role,
      mobile: user.mobile,
      email: user.email,
      address: user.address || "",
      gender: user.gender,
      dob: user.dob.split("T")[0], // Format date to YYYY-MM-DD
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const updateUser = (id) => {
    axios
      .put(`http://localhost:2323/users/${id}`, formData)
      .then(() => {
        setUsers(
          users.map((user) =>
            user.id === id ? { ...user, ...formData } : user
          )
        );
        setEditing(null);
        setFormData({
          name: "",
          role: "",
          mobile: "",
          email: "",
          address: "",
          gender: "",
          dob: "",
        });
      })
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <h1>User List---</h1>
      {users ? (
        <>
          <div>
            {users.map((user) => (
              <div key={user.id} className="border-dark border m-1 container">
                {editing === user.id ? (
                  <div className="p-3">
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      placeholder="Name"
                      className="form-control mb-2"
                    />
                    <select
                      name="role"
                      value={formData.role}
                      onChange={handleChange}
                      className="form-control mb-2"
                    >
                      <option value="Super Admin">Super Admin</option>
                      <option value="Admin">Admin</option>
                      <option value="User">User</option>
                    </select>
                    <input
                      type="tel"
                      name="mobile"
                      value={formData.mobile}
                      onChange={handleChange}
                      placeholder="Mobile"
                      className="form-control mb-2"
                    />
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      placeholder="Email"
                      className="form-control mb-2"
                    />
                    <textarea
                      name="address"
                      value={formData.address}
                      onChange={handleChange}
                      placeholder="Address"
                      className="form-control mb-2"
                    />
                    <select
                      name="gender"
                      value={formData.gender}
                      onChange={handleChange}
                      className="form-control mb-2"
                    >
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </select>
                    <input
                      type="date"
                      name="dob"
                      value={formData.dob}
                      onChange={handleChange}
                      className="form-control mb-2"
                    />
                    <div>
                      <button
                        onClick={() => updateUser(user.id)}
                        className="btn btn-primary me-2"
                      >
                        Save
                      </button>
                      <button
                        onClick={() => setEditing(null)}
                        className="btn btn-secondary"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="p-2">
                    <p>Name: {user.name}</p>
                    <p>Role: {user.role}</p>
                    <p>Mobile: {user.mobile}</p>
                    <p>Email: {user.email}</p>
                    <p>Address: {user.address}</p>
                    <p>Gender: {user.gender}</p>
                    <p>DOB: {new Date(user.dob).toLocaleDateString()}</p>
                    <button
                      onClick={() => handleEdit(user)}
                      className="btn btn-primary"
                    >
                      Edit
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </>
      ) : (
        <></>
      )}
    </div>
  );
};

export default EditUsers;
